query GetMovies($id: Int!) {
  movies(where: { id: { eq: $id } }) {
    id
    title
    overview
    originallanguage
    productioncompanies {
      name
      origincountry
    }
    credits {
      cast {
        name
        character
      }
      crew {
        name
        job
      }
    }
  }
}
